import React, {useState, useEffect} from 'react'
import './App.css';
import axios from 'axios'
import Coin from './Coin'



function App() {

    const [coins, setCoins] = useState([]);
    const [search, setSearch] = useState('');
    const [feadAndGreedIndex, setfeadAndGreedIndex] = useState(0);

    useEffect(() => {
      axios.get('https://api.coingecko.com/api/v3/coins/markets?vs_currency=usd&order=market_cap_desc&per_page=100&page=1&sparkline=false')
        .then(res => {
          setCoins(res.data);
        }).catch( error => {
          console.log(error);
        });

      axios.get('https://api.alternative.me/fng/')
        .then(res => {
          if (res && res.data && res.data.data[0] && res.data.data[0].value) {
            setfeadAndGreedIndex(res.data.data[0].value);
          }
        }).catch(error => {
          console.log(error);
        })
    });

    const handleChange = e => {
      setSearch(e.target.value);
    };

    const filteredCoins = coins.filter(coin =>
      coin.name.toLowerCase().includes(search.toLowerCase())
    );

  return (
    <div className="coin-app">
      <div className="coin-search">
        <h1 className="coin-text">Search a currency</h1>
          <form>
            <input type="text" placeholder="Search" className="coin-input" onChange={handleChange}/>
          </form>
      </div>

      <div class="card">
      </div>
      <div className="card">
        <img src="https://alternative.me/crypto/fear-and-greed-index.png" alt="Latest Crypto Fear & Greed Index" />
        <div className="card-body">
          <h6>Market Cap : 1 </h6>
          <h6>Fear and Greed Index : {feadAndGreedIndex !== 0 ? feadAndGreedIndex : 'N/C'} </h6>
          <h6>BTC Dominance : 1 </h6>
          <h6>ETH Dominance : 1 </h6>
        </div>
      </div>
      {filteredCoins.map(coin => {
        return (
          <Coin 
            key={coin.id} 
            name={coin.name} 
            image={coin.image}   
            symbol={coin.symbol}
            marketcap={coin.market_cap}
            price={coin.current_price}
            priceChange={coin.price_change_percentage_24h}
            volume={coin.total_volume}
          />
        )
      })}
    </div>
  );
}

export default App;
